    private void SaveAdmin(){
        try{
        int a = (int) (100000*Math.random());
        int b = (int) (10000*Math.random());
        String id = "9"+String.valueOf(a);
        String pass = String.valueOf(b);
        String jkel;
        if(rlaki.isSelected()){
            jkel = "Laki-laki";
        }else{
            jkel = "Perempuan";
        }
        String lahir = ctgl.getSelectedItem().toString()+" "+cbulan.getSelectedItem().toString()+" "+ctahun.getSelectedItem().toString();
        
        String toAddress = txtemail.getText();
        String subject = "Autentikasi ID dan Password Admin baru";
        String message = "Selamat datang dan selamat bekerja, berikut adalah ID dan Password anda : \nID = "+id+" \nPassword = "+pass+" \n\nHarap login dengan ID dan Password tersebut.";
        Email email = new SimpleEmail();
        email.setHostName(HOST);
        email.setSmtpPort(PORT);
        email.setAuthenticator(new DefaultAuthenticator(userName, password));
        email.setSSLOnConnect(SSL_FLAG);
        email.setFrom(fromAddress);
        email.setSubject(subject);
        email.setMsg(message);
        email.addTo(toAddress);
        email.send();
        
        try{
            String path = new File(".").getCanonicalPath();
            file = new File(path+"\\image\\default\\Default_Pic_Admin.png");
            FileUtils.copyFileToDirectory(file, new File(path+"\\image\\admin\\"+id));
            //fileUser = new File(path+"\\image\\admin\\"+id+file.getName());
        }catch(IOException e){
            JOptionPane.showMessageDialog(null, "Error \n"+e);
        }
        
        admin = new Admin();
        admin.setID(id);
        admin.setPassword(pass);
        admin.setNama(txtnama.getText());
        admin.setJ_Kelamin(jkel);
        admin.setTgl_Lahir(lahir);
        admin.setEmail(txtemail.getText());
        admin.setNo_Telp(txtnotelp.getText());
        admin.setAlamat(txtalamat.getText());
        admin.setGambar(file.getName());
        
        String sql1 = "insert into admin(id,password,nama,j_kelamin,tgl_lahir,email,no_telp,alamat,gambar) values(?,?,?,?,?,?,?,?,?)";
            PreparedStatement statement1 = connection.prepareStatement(sql1);
            statement1.setString(1, Admin.getID());
            statement1.setString(2, Admin.getPassword());
            statement1.setString(3, Admin.getNama());
            statement1.setString(4, Admin.getJ_Kelamin());
            statement1.setString(5, Admin.getTgl_Lahir());
            statement1.setString(6, Admin.getEmail());
            statement1.setString(7, Admin.getNo_Telp());
            statement1.setString(8, Admin.getAlamat());
            statement1.setString(9, Admin.getGambar());
            statement1.executeUpdate();

            JOptionPane.showMessageDialog(null, "Pendaftaran Berhasil, ID dan Password sudah terkirim ke alamat email anda");
            new Login().setVisible(true);
            this.dispose();
        }catch(EmailException | SQLException e){
            JOptionPane.showMessageDialog(null, "Email tidak valid \n"+e);
        }
    }

