    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(txtnama.getText().equals("") || (rlaki.isSelected()==false && rperempuan.isSelected()==false) || ctgl.getSelectedItem()=="Tanggal" || cbulan.getSelectedItem()=="Bulan" || ctahun.getSelectedItem()=="Tahun" || txtemail.getText().equals("") || txtnotelp.getText().equals("") || txtalamat.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Harap isi semua field");
        }else{
            SaveAdmin();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

